import React, { PureComponent, useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  Button,
  TouchableOpacity,
  TextInput,
  StatusBar,
  Platform,
} from "react-native";
import * as firebase from "firebase";
import { firebaseConfig } from "../../App";
import "firebase/firestore";

// Intialize database
// const app = firebase.initializeApp({ firebaseConfig });

// const db = app.firestore();

export default class SearchScreen extends PureComponent {
  constructor(props) {
    super();
    this.state = {};
  }

  render() {
    // const app = this.props.app;
    // const db = app.firestore();
    // console.log(db);
    return (
      <View style={styles.container}>
        <View style={styles.panel}>
          <View style={styles.panelView}>
            <Image
              style={{
                width: 75,
                height: 75,
              }}
              source={{
                uri: "https://static.thenounproject.com/png/2420251-200.png",
              }}
            />
          </View>
        </View>
        <View style={styles.panel}></View>
        <View
          style={{
            flex: 3,
            backgroundColor: "#F6F0ED",
            borderBottomColor: "grey",
            borderBottomWidth: 0.5,
          }}
        ></View>
        <View style={styles.bottomContainer}>
          <TouchableOpacity style={styles.circle}>
            <Text style={styles.buttonText}> Profile </Text>
          </TouchableOpacity>

          {
            <TouchableOpacity
              style={styles.rectangle}
              //   onPress={() => {
              //     props.navigation.navigate("Skills");
              //   }}
            >
              <Text style={styles.buttonText}> My Skills </Text>
            </TouchableOpacity>
          }
          {
            <TouchableOpacity style={styles.rectangle}>
              <Text style={styles.buttonText}> My Guilds </Text>
            </TouchableOpacity>
          }

          <TouchableOpacity style={styles.circle}>
            <Text style={styles.buttonText}> Quest </Text>
          </TouchableOpacity>

          <TouchableOpacity>
            <Image
              style={styles.addIcon}
              source={{
                uri:
                  "https://icons-for-free.com/iconfiles/png/512/circle+more+plus+icon-1320183136549593898.png",
              }}
            />
          </TouchableOpacity>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  addIcon: {
    width: 50,
    height: 50,
  },
  buttonText: {
    color: "white",
  },
  circle: {
    borderWidth: 1,
    borderColor: "rgba(0,0,0,0.2)",
    alignItems: "center",
    justifyContent: "center",
    width: 50,
    height: 50,
    backgroundColor: "#2F2F2F",
    borderRadius: 50,
  },
  rectangle: {
    borderWidth: 1,
    borderColor: "rgba(0,0,0,0.2)",
    alignItems: "center",
    justifyContent: "center",
    width: 75,
    height: 40,
    backgroundColor: "#2F2F2F",
    borderRadius: 10,
  },
  container: {
    flex: 1,
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
    backgroundColor: "#F6F0ED",
  },
  panel: {
    flex: 1,
    backgroundColor: "white",
    borderBottomColor: "black",
    borderBottomWidth: 1,
  },
  bottomBar: {
    flex: 0.75,
    backgroundColor: "#E2CFCA",
  },
  panelView: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    marginLeft: "5%",
  },
  bottomContainer: {
    flex: 0.5,
    //justifyContent: "flex-end",
    backgroundColor: "#E2CFCA",
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
  },
});
