import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  ScrollView,
  TouchableOpacity,
  //SafeAreaView,
  TextInput,
  Platform,
  StatusBar,
  FlatList,
} from "react-native";
import Post from "../components/SearchScreen/post";

export default function HomeScreen(props) {
  return (
    <View style={styles.container}>
      <View style={styles.topContainer}>
        <View style={styles.top}>
          <View style={styles.notifFormat}>
            <Image
              style={styles.notifIcon}
              source={require("../assets/notification.png")}
            />
          </View>
          <View style={styles.topFormat}>
            <TouchableOpacity
              onPress={() => {
                props.navigation.navigate("Search");
              }}
            >
              <Text style={styles.statusText}>Explore</Text>
            </TouchableOpacity>
            <Text style={styles.statusText}>Home</Text>
            <Text style={styles.statusText}>Guilds</Text>
          </View>
        </View>
      </View>
      {/*Use FlatList*/}
      <ScrollView style={styles.middleContainer}>
        <Post
          imageURL={require("../assets/notification.png")}
          username="Bruce"
          guild="UT Convergent"
          caption="WOW it works"
        ></Post>
        <Post></Post>
        <Post></Post>
      </ScrollView>
      <View style={styles.bottomContainer}></View>
    </View>
  );
}

const styles = StyleSheet.create({
  notifFormat: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "flex-end",
  },
  top: {
    flex: 0.75,
  },
  container: {
    flex: 1,
  },
  topFormat: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
  },
  statusText: {
    textDecorationLine: "underline",
    fontSize: 30,
    fontWeight: "600",
  },
  topContainer: {
    flex: 0.1,
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
    backgroundColor: "#E2CFCA",
    borderBottomColor: "gray",
    borderBottomWidth: 0.5,
  },
  middleContainer: {
    flex: 1,
    borderBottomColor: "gray",
    borderBottomWidth: 0.5,
  },
  notifIcon: {
    width: 25,
    height: 25,
    top: "2.5%",
  },
  bottomContainer: {
    flex: 0.1,
    justifyContent: "flex-end",
    backgroundColor: "#E2CFCA",
  },
});
