import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  Button,
  TouchableOpacity,
  //SafeAreaView,
  TextInput,
  Platform,
  StatusBar,
} from "react-native";
import post from "../components/post";

export default function HomeScreen(props) {
  return (
    <View style={styles.container}>
      <View style={styles.topContainer}>
        <View style={styles.top}>
          <View style={styles.notifFormat}>
            <Image
              style={styles.notifIcon}
              source={require("../assets/notification.png")}
            />
          </View>
          <View style={styles.topFormat}>
            <TouchableOpacity
              onPress={() => {
                props.navigation.navigate("Search");
              }}
            >
              <Text style={styles.statusText}>Explore</Text>
            </TouchableOpacity>
            <Text style={styles.statusText}>Home</Text>
            <Text style={styles.statusText}>Guilds</Text>
          </View>
        </View>
      </View>
      <View style={styles.middleContainer}>
        <View style={styles.postInfo}>
          {/* <View style={styles.profileFormat}>
            <Image
              style={styles.profileIcon}
              source={require("../assets/pfp.png")}
            />
            <View style={styles.pfpInfoFormat}>
              <Text style={styles.usernameText}> Username</Text>
              <Text style={styles.usernameText}> Guild: {"{Guild}"}</Text>
            </View>
          </View> */}
        </View>
        <View style={styles.post}></View>
        <View style={styles.postActions}></View>
      </View>
      <View style={styles.bottomContainer}></View>
    </View>
  );
}

const styles = StyleSheet.create({
  notifFormat: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "flex-end",
  },
  top: {
    flex: 0.75,
  },
  container: {
    flex: 1,
  },
  topFormat: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
  },
  profileFormat: {
    flex: 1,
    left: "5%",
    flexDirection: "row",
    alignItems: "center",
  },
  pfpInfoFormat: {
    flex: 1,
  },
  usernameText: {
    top: "-2.5%",
    fontSize: 20,
  },
  statusText: {
    textDecorationLine: "underline",
    fontSize: 30,
    fontWeight: "600",
  },
  topContainer: {
    flex: 1,
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
    backgroundColor: "#E2CFCA",
  },
  middleContainer: {
    flex: 6,
  },
  profileIcon: {
    width: 75,
    height: 75,
  },
  notifIcon: {
    width: 25,
    height: 25,
    top: "2.5%",
  },
  bottomContainer: {
    flex: 0.6,
    justifyContent: "flex-end",
    backgroundColor: "#E2CFCA",
  },
  postInfo: {
    backgroundColor: "#F6F0ED",
    flex: 1.1,
  },
  post: {
    flex: 4,
  },
  postActions: {
    backgroundColor: "#F6F0ED",
    flex: 1.1,
  },
});
