import React, { useState, PureComponent } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  Button,
  TouchableOpacity,
  TextInput,
} from "react-native";

export default class post extends PureComponent {
  constructor(props) {
    super();
  }
  render() {
    return (
      <View style={styles.container}>
        {/*Post Header*/}
        <View style={styles.postInfo}>
          <View style={styles.profileFormat}>
            <Image
              style={styles.profileIcon}
              source={require("../../assets/pfp.png")}
            />
            <View style={styles.pfpInfoFormat}>
              <Text style={styles.usernameText}> Username</Text>
              <Text style={styles.usernameText}> Guild: {"{Guild}"}</Text>
            </View>
          </View>
        </View>
        {/*Post Body*/}
        <View style={styles.post}>
          <Image
            style={styles.postImage}
            source={require("../../assets/bball.jpg")}
          />
        </View>
        {/*Post Footer*/}
        <View style={styles.postActions}></View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    background: "black",
  },
  profileFormat: {
    flex: 1,
    left: "5%",
    flexDirection: "row",
    alignItems: "center",
  },
  pfpInfoFormat: {
    flex: 1,
  },
  usernameText: {
    top: "-2.5%",
    fontSize: 20,
  },
  profileIcon: {
    width: 75,
    height: 75,
  },
  postInfo: {
    backgroundColor: "black",
    flex: 10,
  },
  post: {
    flex: 4,
    //backgroundColor: "black",
  },
  postImage: {
    width: 100,
    height: 100,
  },
  postActions: {
    backgroundColor: "#F6F0ED",
    flex: 1.1,
  },
});
