import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  Button,
  TouchableOpacity,
  //SafeAreaView,
  TextInput,
  Platform,
  StatusBar,
} from "react-native";

export default function HomeScreen(props) {
  state = {
    search: "",
  };

  return (
    <View style={styles.container}>
      <View style={styles.topContainer}>
        <View style={styles.inputbox}>
          <TextInput
            style={{ textAlign: "center", fontSize: 20 }}
            placeholder="Search"
            placeholderTextColor="#290907"
            onChangeText={(search) => this.setState({ search })}
            value={this.state.search}
          />
        </View>
        <View style={styles.center}>
          <TouchableOpacity
            onPress={() => {
              props.navigation.navigate("Search");
            }}
          >
            <Text style={styles.statusText}> Explore</Text>
          </TouchableOpacity>
          <Text style={styles.statusText}>Home</Text>
          <Text style={styles.statusText}>Guilds</Text>
        </View>
      </View>
      <View style={styles.middleContainer}>
        <View style={styles.postInfo}></View>
        <View style={styles.post}></View>
        <View style={styles.postActions}></View>
      </View>
      <View style={styles.bottomContainer}></View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  center: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-evenly",
  },
  statusText: {
    fontSize: 30,
    fontWeight: "600",
  },
  topContainer: {
    flex: 1.5,
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
    backgroundColor: "#E2CFCA",
  },
  middleContainer: {
    flex: 6,
  },
  bottomContainer: {
    flex: 0.6,
    justifyContent: "flex-end",
    backgroundColor: "#E2CFCA",
  },
  postInfo: {
    backgroundColor: "#F6F0ED",
    flex: 1.1,
  },
  post: {
    flex: 4,
  },
  postActions: {
    backgroundColor: "#F6F0ED",
    flex: 1.1,
  },
});
