import React, { useState, PureComponent } from "react";
import { StyleSheet, Text, View, Image, ScrollView } from "react-native";
import Avatar from "@material-ui/core/Avatar";

export default class post extends PureComponent {
  constructor(pfp, imageURL, username, guild, caption) {
    super();
  }
  render() {
    const app = this.props.app;
    const db = app.firestore();
    console.log(db);
    return (
      <View style={styles.container}>
        {/*Post Header*/}
        <View style={styles.postInfo}>
          <View style={styles.profileFormat}>
            <Image style={styles.profileIcon} source={this.props.pfp} />
            {/* <Avatar
              //style={styles.profileIcon}
              source={require("../../assets/pfp.png")}
            /> */}
            <View style={styles.pfpInfoFormat}>
              <Text style={styles.usernameText}>{this.props.username}</Text>
              <Text style={styles.usernameText}>Guild: {this.props.guild}</Text>
            </View>
          </View>
        </View>
        {/*Post Body*/}
        <View style={styles.post}>
          <View style={styles.center}>
            <Image style={styles.postImage} source={this.props.imageURL} />
          </View>
        </View>
        {/*Post Footer*/}
        <View style={styles.postActions}>
          <View style={styles.comment}>
            <Text style={styles.user}> {this.props.username}</Text>
            <Text style={styles.caption}> {this.props.caption}</Text>
          </View>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  center: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  container: {
    flexDirection: "column",
    flex: 1,
  },
  comment: {
    paddingTop: 10,
    paddingLeft: 10,
    flexDirection: "row",
    flex: 1,
  },
  user: {
    fontWeight: "bold",
  },
  profileFormat: {
    flex: 1,
    left: "5%",
    flexDirection: "row",
    alignItems: "center",
  },
  pfpInfoFormat: {
    flex: 1,
  },
  usernameText: {
    top: "-2.5%",
    fontSize: 20,
  },
  profileIcon: {
    width: 75,
    height: 75,
  },
  postInfo: {
    backgroundColor: "#F6F0ED",
    flex: 1.1,
    borderBottomColor: "gray",
    borderBottomWidth: 0.5,
  },
  post: {
    flex: 6,
    borderBottomColor: "gray",
    borderBottomWidth: 0.5,
  },
  postImage: {
    width: 200,
    height: 200,
  },
  postActions: {
    backgroundColor: "#F6F0ED",
    flex: 2,
    paddingBottom: 100,
    borderBottomColor: "gray",
    borderBottomWidth: 0.5,
  },
});
