import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  Button,
  TouchableOpacity,
  //SafeAreaView,
  TextInput,
  Platform,
  StatusBar,
} from "react-native";

export default function HomeScreen(props) {
  return (
    <View style={styles.container}>
      <View style={styles.topContainer}>
        <View style={styles.notifPos}>
          <Image
            style={styles.notifIcon}
            source={require("../assets/notification.png")}
          />
        </View>
        <View style={styles.topFormat}>
          <TouchableOpacity
            onPress={() => {
              props.navigation.navigate("Search");
            }}
          >
            <Text style={styles.statusText}> Explore</Text>
          </TouchableOpacity>
          <Text style={styles.statusText}>Home</Text>
          <Text style={styles.statusText}>Guilds</Text>
        </View>
      </View>
      <View style={styles.middleContainer}>
        <View style={styles.postInfo}>
          <View style={styles.profile}>
            <Image
              style={styles.profileIcon}
              source={require("../assets/pfp.png")}
            />
          </View>
          <View style={styles.profileFormat}>
            <Text style={styles.usernameText}> Username</Text>
          </View>
        </View>
        <View style={styles.post}></View>
        <View style={styles.postActions}></View>
      </View>
      <View style={styles.bottomContainer}></View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  topFormat: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-evenly",
    //alignItems: "center",
  },
  profileFormat: {
    left: "20%",
    justifyContent: "space-evenly",
  },
  usernameText: {
    fontSize: 20,
  },
  statusText: {
    textDecorationLine: "underline",
    fontSize: 30,
    fontWeight: "600",
  },
  topContainer: {
    flex: 1,
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
    backgroundColor: "#E2CFCA",
  },
  middleContainer: {
    flex: 6,
  },
  notifPos: {
    flexDirection: "row",
    justifyContent: "flex-end",
    paddingTop: "3%",
    paddingRight: "1%",
  },
  profile: {
    top: "25%",
    left: "5%",
  },
  profileIcon: {
    width: 50,
    height: 50,
  },
  notifIcon: {
    width: 25,
    height: 25,
  },
  bottomContainer: {
    flex: 0.6,
    justifyContent: "flex-end",
    backgroundColor: "#E2CFCA",
  },
  postInfo: {
    backgroundColor: "#F6F0ED",
    flex: 1.1,
  },
  post: {
    flex: 4,
  },
  postActions: {
    backgroundColor: "#F6F0ED",
    flex: 1.1,
  },
});
